import React, { useEffect } from 'react';
import css from './Modal.module.css';
import closeIcon from '../../images/icons/x-mark.svg';
import { createPortal } from 'react-dom';

const modalRoot = document.querySelector('#root-modal');

const Modal = ({ title, children, onClose }) => {
  useEffect(() => {
    const handleKeyDown = event => {
      if (event.code === 'Escape') {
        onClose(false);
      }
    };

    const modalRef = useRef(null);

    window.addEventListener('keydown', handleKeyDown);
    document.addEventListener('mousedown', handleClose);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      document.addEventListener('mousedown', handleClose);
    };
  }, [onClose]);

  const handleClose = event => {
    if (modalRef.current && !modalRef.current.contains(event.target)) {
      onClose(false);
    }
  };

  return createPortal(
    <div onClick={handleClose} className={css.backdrop} ref={modalRef}>
      <div
        className={`${css.modal} ${
          title === 'Setting' ? css.settingModal : css.modal
        }`}
        onClick={event => event.stopPropagation()}
      >
        {title && <h3 className={css.title}>{title}</h3>}
        <button className={css.btnClose} onClick={handleClose}>
          <img src={closeIcon} width={24} alt="Close" />
        </button>
        <div className={css.content}>{children}</div>
      </div>
    </div>,
    modalRoot
  );
};

export default Modal;
