import React, { useState, useEffect } from 'react';
import css from './AddWaterModal.module.css';

import { Formik, Form, Field } from 'formik';
// import { object, number, date } from 'yup';

import { ReactComponent as IconPlus } from '../../images/icons/plus-small.svg';
import { ReactComponent as IconMinus } from '../../images/icons/minus-small.svg';

const AddWaterModal = ({ isAddWater, isEditWater }) => {
  const [cleanStatus, setCleanStatus] = useState({
    isAddWater: true,
    isEditWater: true,
  });

  const [waterValueState, setWaterValueState] = useState(0);
  const [time, setTime] = useState(0);

  const getCurrentTime = () => {
    const now = new Date();
    const hours = now.getHours().toString().padStart(2, '0');
    const minutes = Math.floor(now.getMinutes() / 5) * 5;
    const formattedMinutes = minutes.toString().padStart(2, '0');
    console.log(formattedMinutes);
    return `${hours}:${formattedMinutes}`;
  };

  useEffect(() => {
    const now = getCurrentTime();
    setTime(now);
    console.log(time);
  }, [setTime, time]);

  const generateTimeOptions = () => {
    const options = [];

    for (let hours = 0; hours < 24; hours++) {
      for (let minutes = 0; minutes < 60; minutes += 5) {
        const formattedHours = String(hours).padStart(2, '0');
        const formattedMinutes = String(minutes).padStart(2, '0');
        options.push({
          value: `${formattedHours}:${formattedMinutes}`,
          label: `${formattedHours}:${formattedMinutes}`,
        });
      }
    }
    return options;
  };

  const initialValues = {
    waterValue2: 0,
    recordingTime: '00:00',
    waterUsed: 0,
  };

  const handleSubmit = values => {
    console.log('time', time);
    console.log(values);
    setCleanStatus({
      isAddWater: !cleanStatus.isAddWater,
      isEditWater: !cleanStatus.isEditWater,
    });
  };

  const setPreviousAmount = e => {
    const trimmedValue = e.target.value.trim();
    if (trimmedValue === '') {
      setWaterValueState(waterValueState);
    }
  };

  const addAmountOfWater = () => {
    setWaterValueState(Number.parseInt(waterValueState) + 1);
  };

  const minusAmountOfWater = () => {
    if (waterValueState > 0) {
      setWaterValueState(Number.parseInt(waterValueState) - 1);
    }
  };

  return (
    <>
      <p className={css.paragraphs}>Amount of water:</p>
      <div>
        <button type="button" onClick={minusAmountOfWater}>
          <IconMinus />
        </button>
        <input
          min="0"
          type="number"
          // name="waterValue2"
          // onBlur={handleBlur}
          value={waterValueState}
          readOnly
        />
        <button type="button" onClick={addAmountOfWater}>
          <IconPlus />
        </button>
      </div>

      <Formik
        initialValues={{ ...initialValues, recordingTime: time }}
        onSubmit={handleSubmit}
      >
        {({ handleBlur, setFieldValue, values, setValues }) => (
          <Form autoComplete="off">
            {isAddWater && <h2 className={css.headlines}>Choose a value:</h2>}

            <div>
              <p className={css.paragraphs}>Recording time:</p>
              <div>
                {time}
                {/* {generateTimeOptions().map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))} */}
                <Field as="select" name="recordingTime" value={time}>
                  {generateTimeOptions().map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </Field>
              </div>
            </div>
            <h2 className={css.headlines}>
              Enter the value of the water used:
            </h2>
            <Field
              min="0"
              type="number"
              name="waterValue2"
              onBlur={setPreviousAmount}
              // value={waterValue}
              onChange={e => {
                const newValue = e.target.value;
                setWaterValueState(newValue);
              }}
              onClick={e => {
                const newValue = e.target.value === '0' ? '' : e.target.value;
                if (newValue >= 0) {
                  setFieldValue('waterValue', newValue);
                  setWaterValueState(newValue);
                }
              }}
            />
            <button type="submit">Save</button>
          </Form>
        )}
      </Formik>
    </>
  );
};

export default AddWaterModal;

